import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, RegisterPayload } from 'src/app/core/services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
  firstName: string = '';
  surname: string = '';
  contact: string = '';
  email: string= '';
  password: string = '';
  confirmPassword: string = '';
  day: number = 1;
  month: number = 1;
  year: number = 2000;
  gender: string = 'Female';
  days: number[] = [];
  months: { name: string, value: number }[] = [
    { name: 'Jan', value: 1 },
    { name: 'Feb', value: 2 },
    { name: 'Mar', value: 3 },
    { name: 'Apr', value: 4 },
    { name: 'May', value: 5 },
    { name: 'Jun', value: 6 },
    { name: 'Jul', value: 7 },
    { name: 'Aug', value: 8 },
    { name: 'Sep', value: 9 },
    { name: 'Oct', value: 10 },
    { name: 'Nov', value: 11 },
    { name: 'Dec', value: 12 }
  ];
  years: number[] = [];

  constructor(private authService:AuthService, private router:Royter)
  { }

  ngOnInit(): void {
    for (let i = 1; i <= 31; i++) {
      this.days.push(i);
    }
    const currentYear = new Date().getFullYear();
    for (let i = currentYear; i >= currentYear - 100; i--) {
      this.years.push(i);
    }

  }

  user:RegisterPayload={
    firstName:'',
    lastName:'',
    email:'',
    password:'',
    dateOfBirth:'',
    gender:'',
    profilePictureUrl:'',
    bio:''
  };

  onRegister() {
    if(this.password!=this.confirmPassword){
      alert("Password doesn't match!!")
    }
    else{
      this.user={
        firstName: this.firstName,
        lastName: this.surname,
        email: this.email,
        password: this.password,
        dateOfBirth: `${this.year}-${this.month}-${this.day}`,
        gender: this.gender,
        profilePictureUrl:'',
        bio:''
      }
      this.authService.registerUser(this.user).subscribe({
        next:res=>{
          alert("Registration Successful!");
          console.log(res);
          this.rout
        }
      })

    }
  }

}
