import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username = '';
  password = '';
  constructor(private router:Router)
  {}

  login() {
    if (this.username && this.password){
      this.router.navigate(['/home']);
    } else {
      alert('Please enter username and password');
    }
  }

}
<div class="auth-container">
    <h1>Connecto</h1>
    <p>Weaving the world together</p>

    <form class="form">
     <h3>Log in to Connecto</h3>   
    <div class="auth-box">
        <input type="text" placeholder="Email address or phone number" [(ngModel)]="username" />
        <input type="password" placeholder="Password" [(ngModel)]="password" />
     <button (click)="login()">Log in</button>   

    <hr />

   <button routerLink="/auth/register" class="create-btn">Create new account</button>
</div>

</form>
</div>
