import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, LoginPayload } from 'src/app/core/services/Login/authLogin.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  credentails:LoginPayload={
    username:'',
    password:''
  }
  constructor(private authService: AuthService,private router:Router, private cdf:ChangeD)
  {}

  login() :void{
    this.authService.login(this.credentails).subscribe({
      next:res=>{
        alert('Login Successful');
        this.router.navigate(['/']);
        console.log(res);
      },
      error:err=>{
        alert("Invalid Credentials");
        console.log(err);
      }
    })
  }

}
