import { Injectable } from '@angular/core';

import { HttpClient } from '@angular/common/http';

import { Observable } from 'rxjs';

export interface Friend {

  userId: number;

  username: string;

  profilePictureUrl?: string;
  bio?:string;

}

export interface Request{
  requestId:number;
  sender:Friend;
  receiver:Friend;
}

@Injectable({

  providedIn: 'root'

})

export class FriendsService {

  private apiUrl = 'http://localhost:8765/api/friend-requests';

  constructor(private http: HttpClient) {}

  getFriends(userId:any): Observable<Friend[]> {
    return this.http.get<Friend[]>(`${this.apiUrl}/friends/${userId}`);
  }

  getFriendRequestsReceived(userId:any): Observable<Request[]> {
    return this.http.get<Request[]>(`${this.apiUrl}/received/${userId}`);
  }

  getFriendRequestsSent(userId:any): Observable<Request[]> {
    return this.http.get<Request[]>(`${this.apiUrl}/sent/${userId}`);
  }

  sendFriendRequest(userId:any, receiverId: number): Observable<any> {

    return this.http.post(`${this.apiUrl}/send?senderId=${userId}&receiverId=${receiverId}`,{});

  }

  acceptFriendRequest(requestId: number): Observable<any> {

    return this.http.put(`${this.apiUrl}/accept/${requestId}`, {});

  }

  rejectFriendRequest(requestId: number): Observable<any> {

    return this.http.put(`${this.apiUrl}/reject/${requestId}`, {responseTyp});

  }

  cancelFriendRequest(userId: any,receiverId:number): Observable<any> {

    return this.http.delete(`${this.apiUrl}/cancel?senderId=${userId}&receiverId=${receiverId}`,{responseType:'text' as 'json'});

  }

  findFriends(userId: any): Observable<Friend[]> {

    return this.http.get<Friend[]>(`${this.apiUrl}/friends/find/${userId}`);

  }

  unFriend(userId:any,receiverId:number):Observable<any>{
      return this.http.delete(`${this.apiUrl}/friends/delete?userId${userId}&friendId${receiverId}`);
  }

  findMutualFriend(userId:any,receiverId:any): Observable<Friend[]>{
    return this.http.get<Friend[]>(`${this.apiUrl}/friends/find?userId=${userId}&friendId=${receiverId}`);
  }

}
