import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface User {
  userId: number;            // ✅ match your backend DTO field name
  username: string;
  firstName?: string;
  lastName?: string;
  dateOfBirth?: string;
  bio?: string;
  profilePictureUrl?: string;
}

@Injectable({
  providedIn: 'root'
})
export class UserService {
  private baseUrl = 'http://localhost:8765/api/users';

  constructor(private http: HttpClient) {}

  // ✅ Get user by ID
  getUser(id: number): Observable<User> {
    return this.http.get<User>(`${this.baseUrl}/${id}`);
  }

  // ✅ Update user
  updateUser(id: number, user: User): Observable<any> {
    return this.http.put(`${this.baseUrl}/${id}`, user, { responseType: 'text' });
  }

  // ✅ NEW: get currently logged-in user from backend
  // requires backend endpoint -> @GetMapping("/me") in UserController
  getMe(): Observable<User> {
    return this.http.get<User>(`${this.baseUrl}/me`);
  }
}
