import { Component } from '@angular/core';

@Component({
  selector: 'app-friends',
  templateUrl: './friends.component.html',
  styleUrls: ['./friends.component.css']
})
export class FriendsComponent {
  isActive = true;
  toggleSidebar() {
    this.isActive = !this.isActive;
  }
}
<div id="sidebar" [class.active]="isActive" class="bg-dark text-white">
  <a [routerLink]="['/friends/find-friends']" class="d-block p-3 text-white text-decoration-none" routerLinkActive="active">Home</a>
  <a [routerLink]="['/friends/friend-list']" class="d-block p-3 text-white text-decoration-none">Profile</a>
  <a [routerLink]="['/friends/friend-requests']" class="d-block p-3 text-white text-decoration-none">Messages</a>
</div>
<nav class="navbar navbar-expand-lg navbar-light"> 
    <a class="navbar-brand text-bold text-white" [routerLink]="['/home']" routerLinkActive="active">HOME</a>  
    <div class="navbar-nav ms-auto">
            <a class="nav-item nav-link" [routerLink]="['/profile']" routerLinkActive="active">PROFILE</a>  
            <a class="nav-item nav-link" [routerLink]="['/friends']" routerLinkActive="active">FRIENDS</a>  
            <a class="nav-item nav-link" [routerLink]="['/notifications']" routerLinkActive="active">NOTIFICATIONS</a>  
            <a class="nav-item nav-link" [routerLink]="['/auth']" routerLinkActive="active">LOGOUT</a>  



