import { Component, OnInit } from '@angular/core';
import { ProfileService } from 'src/app/main/services/profile.service';

@Component({
  selector: 'app-profile-view',
  templateUrl: './profile-view.component.html',
  styleUrls: ['./profile-view.component.css']
})
export class ProfileViewComponent implements OnInit {
  user: any = {};
  posts: any[] = [];
  selectedLikes:string[]=[]
  showLikesModel=false
  showCommentsModel:boolean=false
  selectedComments:any[]=[]

  constructor(private profileService:ProfileService){}

  ngOnInit(): void {
    // ‚úÖ Static user data
    this.user = {
      name: 'Anjali Sharma',
      location: 'Bangalore, India',
      bio: 'Infosys employee üíª',
      avatar: 'assets/images/default-avatar.png',
      cover: 'assets/images/cover-placeholder.jpg',
      friends: 65,
      photos: 43,
      comments: 21
    };

    // ‚úÖ Static posts data
    this.posts = [
      {
        id: 1,
        content: 'Excited to start working on Angular project!',
        likedUsers: [],
        comments: [],
        showComments: false
      },
      {
        id: 2,
        content: 'Weekend vibes üå∏',
        likedUsers: [],
        comments: [],
        showComments: false
      }
    ];
    this.posts.forEach(p=>this.loadLikes(p));
  }

 loadLikes(post: any): void {
    this.profileService.getLikesByPost(post.id).subscribe({
      next: (likes) => {
        post.likedUsers = likes.map(like => like.fullName); // assuming LikeDTO has username
      },
      error: (err) => console.error('Error fetching likes:', err)
    });
  }

  toggleLikes(post:any){
   if(!post.showLikes){
    this.profileService.getLikesByPost(post.id).subscribe(likes=>{
      post.likedUsers=likes.map(i=>i.fullName);
      post.showLikes=true
    })
   }
   else{
    post.showLikes=false
   }
  }
  

  likePost(postId: number): void {
    const post = this.posts.find(p => p.id === postId);
    if (post && !post.likedUsers.includes('You')) {
      post.likedUsers.push('You');
    }
  }

  editPost(post: any): void {
    const updated = prompt('Edit post:', post.content);
    if (updated) {
      post.content = updated;
    }
  }

  deletePost(postId: number): void {
   if(confirm('Are you sure You want to delete this post?')){
    this.profileService.deletePost(postId).subscribe({
      next:()=>{
        this.posts=this.posts.filter(p=>p.id !==postId)
      },
      error:(err)=>{
        console.error('Error deleting post: ',err)
      }
    })
   }
  }

  viewComments(post:any):void{
    if(!post.showComments){
      this.profileService.getComments(post.id).subscribe(comments=>{
    this.selectedComments=comments
    this.showCommentsModel=true
    })
    }else{
      this.showCommentsModel=false
    }
    
  }
  closeCommentsModel():void{
    this.showCommentsModel=false
    this.selectedComments=[]
  }

  toggleComments(post: any): void {
    post.showComments = !post.showComments;
  
  }
}
