import { Component } from '@angular/core';
import { Friend } from 'src/app/core/services/Friends/friends.service';

@Component({
  selector: 'app-friend-request-received',
  templateUrl: './friend-request-received.component.html',
  styleUrls: ['./friend-request-received.component.css']
})

export class FriendRequestsReceivedComponent implements OnInit {

  requests: Friend[] = [];

  constructor(private friendsService: FriendsService) {}

  ngOnInit(): void {

    this.loadRequests();

  }

  loadRequests(): void {

    this.friendsService.getReceivedRequests().subscribe({

      next: data => (this.requests = data),

      error: err => console.error('Error loading received requests', err)

    });

  }

  accept(id: number): void {

    this.friendsService.acceptFriendRequest(id).subscribe(() => this.loadRequests());

  }

  reject(id: number): void {

    this.friendsService.rejectFriendRequest(id).subscribe(() => this.loadRequests());

  }

}
