import { Component, OnInit } from '@angular/core';
import { Friend, FriendsService } from 'src/app/core/services/Friends/friends.service';

@Component({
  selector: 'app-friend-request-sent',
  templateUrl: './friend-request-sent.component.html',
  styleUrls: ['./friend-request-sent.component.css']
})

export class FriendRequestSentComponent implements OnInit {

  sentRequests: Friend[] = [];

  constructor(private friendsService: FriendsService) {}

  ngOnInit(): void {

    this.loadRequests();

  }

  loadRequests(): void {

    this.friendsService.getFriendRequestsSent(localStorage.getItem()).subscribe({

      next: data => (this.sentRequests = data),

      error: err => console.error('Error loading sent requests', err)

    });

  }

  cancel(id: number): void {

    this.friendsService.cancelFriendRequest(id).subscribe(() => this.loadRequests());

  }

}
