import { Component, OnInit } from '@angular/core';
import { Friend, FriendsService } from 'src/app/core/services/Friends/friends.service';
import { AuthService } from 'src/app/core/services/Login/authLogin.service';


@Component({
  selector: 'app-find-friends',
  templateUrl: './find-friends.component.html',
  styleUrls: ['./find-friends.component.css']
})
export class FindFriendsComponent implements OnInit {

  allFriends: Friend[] = [];

  filteredFriends: Friend[] = [];

  searchText: string = '';

  expanded: { [userId: number]: boolean } = {};

  mutualFriendsMap: { [userId: number]: Friend[] } = {}; // store loaded lists

  constructor(private friendsService: FriendsService) {}

  ngOnInit(): void {

    this.loadFriends();

  }

  loadFriends(): void {

    this.friendsService.findFriends(localStorage.getItem("userId")).subscribe({

      next: (data) => {


        this.filteredFriends = data;

      },

      error: (err) => console.error('Error loading friends', err)

    });

  }

  onSearchChange(): void {

    const text = this.searchText.toLowerCase();

    this.filteredFriends = this.allFriends.filter(friend =>

      friend.username.toLowerCase().includes(text)

    );

  }

  toggleDropdown(friendId: number): void {

    this.expanded[friendId] = !this.expanded[friendId];

    if (this.expanded[friendId] && !this.mutualFriendsMap[friendId]) {

      this.friendsService.findMutualFriend(localStorage.getItem("userId"),friendId).subscribe({

        next: (data) => (this.mutualFriendsMap[friendId] =data),

        error: (err) => console.error('Error loading mutual friends', err)

      });

    }

  }

  sendRequest(friend: Friend): void {

    this.friendsService.sendFriendRequest(localStorage.getItem('userId'),friend.userId).subscribe({

      next: () => {

        alert(`Friend request sent to ${friend.username}`);

        this.filteredFriends = this.filteredFriends.filter(f => f.userId !== friend.userId);

      },

      error: (err) => {

        alert('Failed to send request');

        console.error(err);

      }

    });

  }

}
