import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, LoginPayload } from 'src/app/core/services/Login/authLogin.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  credentails:LoginPayload={
    username:'',
    password:''
  }
  constructor(private authService: AuthService,private router:Router)
  {}

  login() :void{
    this.authService.login({
      username:this.credentails.username,
      password:this.credentails.password
    }).subscribe({
      next:res=>{
        alert('Login Successful');
        this.router.navigate(['/home']);
        console.log(res);
      },
      error:err=>{
        console.log(this.credentails.username);
        console.log(this.credentails.password);
        alert("Invalid Credentials");
        console.log(err);
      }
    })
  }

}
