import { Component } from '@angular/core';

@Component({
  selector: 'app-friends',
  templateUrl: './friends.component.html',
  styleUrls: ['./friends.component.css']
})
export class FriendsComponent {
  isActive = false;
  toggleSidebar() {
    this.isActive = !this.isActive;
  }
}
<div id="sidebar" [class.active]="isActive" class="bg-dark text-white">
  <a href="#" class="d-block p-3 text-white text-decoration-none">Home</a>
  <a href="#" class="d-block p-3 text-white text-decoration-none">Profile</a>
  <a href="#" class="d-block p-3 text-white text-decoration-none">Messages</a>
  <a href="#" class="d-block p-3 text-white text-decoration-none">Settings</a>
</div>


<div id="content" [class.sidebar-open]="isActive">
  <button class="btn btn-primary m-3" (click)="toggleSidebar()">☰ Toggle Sidebar</button>
  <div class="container mt-4">
    <h1>Welcome</h1>
    <p>This is the main content area. The sidebar will push this content.</p>
  </div>
</div>
