import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-profile-view',
  templateUrl: './profile-view.component.html',
  styleUrls: ['./profile-view.component.css']
})
export class ProfileViewComponent implements OnInit {
  user: any = {};
  posts: any[] = [];

  ngOnInit(): void {
    // ‚úÖ Static user data
    this.user = {
      name: 'Anjali Sharma',
      location: 'Bangalore, India',
      bio: 'Infosys employee üíª',
      avatar: 'assets/images/default-avatar.png',
      cover: 'assets/images/cover-placeholder.jpg',
      friends: 65,
      photos: 43,
      comments: 21
    };

    // ‚úÖ Static posts data
    this.posts = [
      {
        id: 1,
        content: 'Excited to start working on Angular project!',
        likedUsers: ['Rahul', 'Sneha'],
        comments: [
          { id: 1, authorName: 'Rahul', text: 'Great going!' },
          { id: 2, authorName: 'Sneha', text: 'All the best üëç' }
        ],
        showComments: false
      },
      {
        id: 2,
        content: 'Weekend vibes üå∏',
        likedUsers: [],
        comments: [],
        showComments: false
      }
    ];
  }

  likePost(postId: number): void {
    const post = this.posts.find(p => p.id === postId);
    if (post && !post.likedUsers.includes('You')) {
      post.likedUsers.push('You');
    }
  }

  editPost(post: any): void {
    const updated = prompt('Edit post:', post.content);
    if (updated) {
      post.content = updated;
    }
  }

  deletePost(postId: number): void {
    this.posts = this.posts.filter(p => p.id !== postId);
  }

  toggleComments(post: any): void {
    post.showComments = !post.showComments;
  }
  viewLikes(post:any):void{
    if(post.likedByUsers && post.likedByUsers.length>0){
      alert('Liked By: '+post.likedByUsers.join)
    }else{
      alert('no likes yet')
    }
  }
}
