import { Component } from '@angular/core';

@Component({
  selector: 'app-upload-post',
  templateUrl: './upload-post.component.html',
  styleUrls: ['./upload-post.component.css']
})
export class UploadPostComponent {
mediaPreview: string | ArrayBuffer | null = null;
description: string = '';
privacy: string = 'public';

onMediaSelected(event: any) {
  const file = event.target.files[0];
  if (file) {
    const reader = new FileReader();
    reader.onload = () => {
      this.mediaPreview = reader.result;
    };
    reader.readAsDataURL(file);
  }
}

isImage(file: any) {
  return typeof file === 'string' && file.startsWith('data:image');
}

isVideo(file: any) {
  return typeof file === 'string' && file.startsWith('data:video');
}

postContent() {
  // handle the posting logic here
  console.log('Media:', this.mediaPreview);
  console.log('Description:', this.description);
  console.log('Privacy:', this.privacy);
}

cancelPost() {
  this.mediaPreview = null;
  this.description = '';
  this.privacy = 'public';
}
}
