import { Component } from '@angular/core';

@Component({
  selector: 'app-feed',
  templateUrl: './feed.component.html',
  styleUrls: ['./feed.component.css']
})
export class FeedComponent {


  posts = [
    { id: 1, author: 'Alice', content: 'Hello, this is my first post!' },    
    { id: 2, author: 'Bob', content: 'Good morning everyone' },
    { id: 3, author: 'Charlie', content: 'Angular project is fun!' }
  ];

}
<div class="feed">
    <app-post-card></app-post-card>
    <app-post-card></app-post-card>
</div>
