import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  name = '';
  email = '';
  password = '';
  dob = '';
  gender = '';

  constructor(private router: Router)
  {}

  register() {
    if (this.name && this.email && this.password) {
      this.router.navigate(['/auth/confirm'], { queryParams: { number: this.email } });
    } else {
      alert('Please fill all details');
    }
  }


}
<div class="auth-container">
    <h1>Connecto</h1>
    <p>Create a new account</p>

    <div class="auth-box">
        <input type="text" placeholder="Full name" [(ngModel)]="name" />
        <input type="email" placeholder="Email address" [(ngModel)]="email" />
        <input type="password" placeholder="Password" [(ngModel)]="password" />
        <input type="date" [(ngModel)]="dob" />

        <select [(ngModel)]="gender">
            <option value="">Select Gender</option>
            <option value="M">Male</option>
            <option value="F">Female</option>
        </select>
        
        <button (click)="register()">Sign Up</button>
        </div>
        </div>
