import { Component, OnInit } from '@angular/core';
import { Friend, FriendsService } from 'src/app/core/services/Friends/friends.service';
import { AuthService } from 'src/app/core/services/Login/authLogin.service';

interface SuggestedFriend {
  
  id: number;
  
  name: string;
  
  avatar: string;
  
  requestSent: boolean;
  
}
@Component({
  selector: 'app-find-friends',
  templateUrl: './find-friends.component.html',
  styleUrls: ['./find-friends.component.css']
})
export class FindFriendsComponent implements OnInit {

  allFriends: Friend[] = [];

  filteredFriends: Friend[] = [];

  searchText: string = '';

  constructor(private friendsService: FriendsService, private authService:AuthService) {}
  userId:number=0;
  ngOnInit(): void {
    var token=this.authService.getToken();
    try {
      console.log(JSON.parse(atob(token.split('.')[1])));

    } catch (e) {

      return null;

    }
    
    this.loadFriends();

  }

  loadFriends(): void {

    this.friendsService.findFriends(1).subscribe({

      next: (data) => {

        this.allFriends = data;

        this.filteredFriends = data;

      },

      error: (err) => console.error('Error loading friends', err)

    });

  }

  onSearchChange(): void {

    const text = this.searchText.toLowerCase();

    this.filteredFriends = this.allFriends.filter(friend =>

      friend.name.toLowerCase().includes(text)

    );

  }

  sendRequest(friend: Friend): void {
    this.friendsService.sendFriendRequest(friend.id,1).subscribe({
      next: () => {
        alert(`Friend request sent to ${friend.name}`);
        // Optionally remove from list
        this.filteredFriends = this.filteredFriends.filter(f => f.id !== friend.id);
      },
      error: (err) => {
        alert('Failed to send request');
        console.error(err);
      }
    });
  }
}
<div class="container mt-3">

  <h3>Find Friends</h3>


  <input

    type="text"

    class="form-control mb-3"

    placeholder="Search for friends..."

    [(ngModel)]="searchText"

    (input)="onSearchChange()"

  />


  <div *ngIf="filteredFriends.length > 0; else noResults">

    <ul class="list-group">

      <li

        *ngFor="let friend of filteredFriends"

        class="list-group-item d-flex justify-content-between align-items-center"

      >

        <div class="d-flex align-items-center">

          <img

            [src]="friend.profilePic || 'assets/default-avatar.png'"

            class="rounded-circle me-2"

            width="40"

            height="40"

          />

          <!-- <div>

            <strong>{{ friend.name }}</strong><br />

            <small *ngIf="friend.mutualFriends">

              {{ friend.mutualFriends }} mutual friends

            </small>

          </div> -->

        </div>

        <button class="btn btn-primary btn-sm" (click)="sendRequest(friend)">

          <i class="bi bi-person-plus"></i> Add Friend

        </button>

      </li>

    </ul>

  </div>

  <ng-template #noResults>

    <p class="text-muted">No friends found.</p>

  </ng-template>

</div>
