import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FriendsService } from 'src/app/core/services/Friends/friends.service';

@Component({
  selector: 'app-view-person-profile',
  templateUrl: './view-person-profile.component.html',
  styleUrls: ['./view-person-profile.component.css']
})
export class ViewPersonProfileComponent implements OnInit{


  username!: string;

  profile: any;

  loading = true;

  constructor(private route: ActivatedRoute, private friendsService: FriendsService) {}

  ngOnInit(): void {

    this.route.paramMap.subscribe(params => {

      this.username = params.get('username')!;

      this.fetchProfile();

    });

  }

  fetchProfile(): void {

    this.friendsService.getProfile(this.username).subscribe({

      next: (data) => {

        this.profile = data;

        this.loading = false;

      },

      error: (err) => {

        console.error('Error fetching profile', err);

        this.loading = false;

      }

    });

  }

  addFriend(): void {

    this.friendsService.sendFriendRequest(,this.profile.userId).subscribe(() => {

      this.profile.relationshipStatus = 'REQUEST_SENT';

    });

  }

  unfriend(): void {

    this.friendsService.unfriend(this.profile.id).subscribe(() => {

      this.profile.relationshipStatus = 'NOT_FRIEND';

    });

  }

  acceptRequest(): void {

    this.friendsService.acceptFriendRequest(this.profile.id).subscribe(() => {

      this.profile.relationshipStatus = 'FRIEND';

    });

  }

}
