import { Component, OnInit } from '@angular/core';
import { FriendRequest } from './FriendRequest';

@Component({
  selector: 'app-friend-requests',
  templateUrl: './friend-requests.component.html',
  styleUrls: ['./friend-requests.component.css']
})
export class FriendRequestsComponent implements OnInit{
  friendRequests:FriendRequest[]=[];
  friends:FriendRequest[]=[];

  ngOnInit(): void {
      this.friendRequests=[
        {id:1,name:"Alice Johnson1",avatar:''},
        {id:2,name:"Alice Johnson2",avatar:''},
        {id:3,name:"Alice Johnson3",avatar:''}
      ]
  }

  acceptRequest(request:FriendRequest):void{
    this.friendRequests=this.friendRequests.filter(r=>r.id!==request.id);
    this.friends.push(request);
    alert(`${request.name} is now your friend!`);
  }

  declineRequest(request:FriendRequest):void{
    this.friendRequests=this.friendRequests.filter(r=>r.id!==request.id);
    alert(`You declined ${request.name}'s request`);
  }
}
