import { Component } from '@angular/core';

@Component({
  selector: 'app-profile-view',
  templateUrl: './profile-view.component.html',
  styleUrls: ['./profile-view.component.css']
})
export class ProfileViewComponent {
  user = {
    name: 'Anjali Sharma',
    bio: 'Infosys employee ðŸ’»',
    avatar: 'assets/images/default-avatar.png',
    cover: 'assets/images/cover-placeholder.jpg'

  };

  posts = [
    { id: 1, content: 'Excited to start working on Angular project!' },
    { id: 2, content: 'Weekend vibes' }
  ];

}
<div class="profile">
    <div class="cover-photo">
        <img [src]="user.cover" alt="Cover Photo">
</div>     

<div class="profile-info">
    <img class="avatar" [src]="user.avatar" alt="Avatar">
    <h2>{{ user.name }}</h2>
    <p>{{ user.bio }}</p>

    <a routerLink="/profile/edit" class="btn">Edit Profile</a>
    </div>

    <h3>My Posts</h3>
    <app-post-card *ngFor="let p of posts" [post]="p"></app-post-card>
</div>
