import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Like } from 'src/app/models/like.model';

@Injectable({
  providedIn: 'root'
})
export class ProfileService {
  private baseUrl = 'http://localhost:8765'; // ðŸ‘‰ backend URL

  constructor(private http: HttpClient) {}

  // ---- User ----
  getUser(userId: number): Observable<any> {
    return this.http.get(`${this.baseUrl}/api/users/${userId}`);
  }

  getLikesByPost(postId:number):Observable<Like[]>{
    return this.http.get<Like[]>(`${this.baseUrl}/api/likes/${postId}`)
  }

  updateUser(userId: number, user: any): Observable<any> {
    return this.http.put(`${this.baseUrl}/api/users/${userId}`, user);
  }

  // ---- Posts ----
  getUserPosts(userId: number): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/api/posts/user/${userId}`);
  }

  createPost(userId: number, post: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/api/posts/userpost/${userId}`, post);
  }

  updatePost(postId: number, post: any): Observable<any> {
    return this.http.put(`${this.baseUrl}/api/posts/${postId}`, post);
  }

  deletePost(postId: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/api/posts/${postId}`);
  }

  // ---- Likes ----
  likePost(dto: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/api/likes`, dto);
  }

  unlikePost(postId: number, userId: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/api/likes/${postId}/user/${userId}`);
  }

  // ---- Comments ----
  getComments(postId: number): Observable<any[]> {
    return this.http.get<any[]>(`${this.baseUrl}/comments/postComment/${postId}`);
  }

  addComment(comment: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/comments`, comment);
  }

  updateComment(commentId: number, userId: number, content: string): Observable<any> {
    return this.http.put(
      `${this.baseUrl}/comments/${commentId}?userId=${userId}&content=${content}`, {}
    );
  }

  deleteComment(commentId: number, userId: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/comments/${commentId}?userId=${userId}`);
  }
}
