import { Component, OnInit } from '@angular/core';
interface SuggestedFriend {

  id: number;

  name: string;

  avatar: string;

  requestSent: boolean;

}

@Component({
  selector: 'app-find-friends',
  templateUrl: './find-friends.component.html',
  styleUrls: ['./find-friends.component.css']
})
export class FindFriendsComponent implements OnInit {

  suggestedFriends: SuggestedFriend[] = [];

  ngOnInit(): void {

    // Dummy suggested friends (later: fetch from backend)

    this.suggestedFriends = [

      { id: 1, name: 'Emma Watson', avatar: 'assets/images/default-avatar.png', requestSent: false },

      { id: 2, name: 'Chris Evans', avatar: 'assets/images/default-avatar.png', requestSent: false },

      { id: 3, name: 'Scarlett Johansson', avatar: 'assets/images/default-avatar.png', requestSent: false }

    ];

  }

  toggleRequest(friend: SuggestedFriend): void {

    friend.requestSent = !friend.requestSent;

    if (friend.requestSent) {

      alert(`Friend request sent to ${friend.name}`);

    } else {

      alert(`Friend request to ${friend.name} canceled`);

    }

  }

}
