import { Component } from '@angular/core';
import { AuthService } from 'src/app/core/services/Login/authLogin.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  constructor(private authService:AuthService){}
  
  LoginCheck():boolean{
    if(this.authService.getToken()) return true;
    return false;
  }
  LogOut():void{
    this.authService.logout();
    console.log("chill")
  }
}
<nav class="navbar navbar-expand-lg navbar-light"> 
    <a class="navbar-brand text-bold text-white" [routerLink]="['/home']" routerLinkActive="active">Connecto</a>  
    <div class="navbar-nav ms-auto" *ngIf="!LoginCheck(); else elseBlock">
      <span >
         <a class="nav-item nav-link" [routerLink]="['/auth']" routerLinkActive="active">LOGIN</a>
         <a class="nav-item nav-link" [routerLink]="['/auth/register']" routerLinkActive="active">REGISTER</a>
      </span>
 <!-- <a routerLink="/home" routerLinkActive="active" class="navbar-brand">Home</a>
   <div class="navbar-nav"> 
    <a routerLink="/profile" routerLinkActive="active" class="nav-item nav-link">Profile</a>
    <a routerLink="/friends" routerLinkActive="active" class="nav-item nav-link">Friends</a>
    <a routerLink="/notifications" routerLinkActive="active" class="nav-item nav-link">Notifications</a>
    <a routerLink="/auth" routerLinkActive="active" class="nav-item nav-link">Logout</a> -->
   </div> 
</nav>
