import { Component, OnInit } from '@angular/core';
import { Friends } from '../Friends';

@Component({
  selector: 'app-friend-list',
  templateUrl: './friend-list.component.html',
  styleUrls: ['./friend-list.component.css']
})
export class FriendListComponent implements OnInit{
  constructor(private friendService:Fr)

  friends:Friends[]=[];
  ngOnInit(): void {

    this.loadRequests();

  }

  loadRequests(): void {

    this.friendsService.getFriendRequestsReceived(localStorage.getItem('userId')).subscribe({

      next: data => {this.requests=data;
      },

      error: err => console.error('Error loading received requests', err)

    });

  }
  
  viewProfile(friend:Friends):void{
    alert(`Viewing profile of ${friend.name}`)
  }

  unfriend(friend:Friends):void{
    this.friends=this.friends.filter(f=>f.id!==friend.id);
  }
}
