import { Injectable } from '@angular/core';

import { HttpClient } from '@angular/common/http';

import { Observable } from 'rxjs';

export interface Friend {

  userId: number;

  username: string;

  profilePictureUrl?: string;
  bio?:string;

}

export interface Request{
  requestId:number;
  sender:UserDTO;
  receiver:UserDTO;
}

export interface UserDTO{
  userId:number,
  username:string,
  email?:string,
  bio?:string,
  firstName:string,
  lastName:string,
  dateOfBirth?:Date,
  gender?:string,
  profilePictureUrl?:string,
  relationshipStatus?:string
} 

@Injectable({

  providedIn: 'root'

})

export class FriendsService {

  private apiUrl = 'http://localhost:8765/api/friend-requests';

  constructor(private http: HttpClient) {}

  getFriends(userId:any): Observable<UserDTO[]> {
    return this.http.get<UserDTO[]>(`${this.apiUrl}/friends/${userId}`);
  }

  getFriendRequestsReceived(userId:any): Observable<Request[]> {
    return this.http.get<Request[]>(`${this.apiUrl}/received/${userId}`);
  }

  getFriendRequestsSent(userId:any): Observable<Request[]> {
    return this.http.get<Request[]>(`${this.apiUrl}/sent/${userId}`);
  }

  sendFriendRequest(userId:any, receiverId: number): Observable<any> {

    return this.http.post(`${this.apiUrl}/send?senderId=${userId}&receiverId=${receiverId}`,{});

  }

  acceptFriendRequest(userId: number,receiverId:any): Observable<any> {

    return this.http.put(`${this.apiUrl}/accept?senderId=${userId}&receiverId=${receiverId}`, {responType:'text' as 'json'});

  }

  rejectFriendRequest(userId: number,receiverId:any): Observable<any> {

    return this.http.put(`${this.apiUrl}/reject?senderId=${userId}&receiverId=${receiverId}`, {responseType:'text' as 'json'});

  }

  cancelFriendRequest(userId: any,receiverId:number): Observable<any> {

    return this.http.delete(`${this.apiUrl}/cancel?senderId=${userId}&receiverId=${receiverId}`,{responseType:'text' as 'json'});

  }

  findFriends(userId: any): Observable<UserDTO[]> {

    return this.http.get<UserDTO[]>(`${this.apiUrl}/friends/find/${userId}`);

  }

  unFriend(userId:any,receiverId:number):Observable<any>{
      return this.http.delete(`${this.apiUrl}/friends/delete?userId=${userId}&friendId=${receiverId}`);
  }

  findMutualFriend(userId:any,receiverId:any): Observable<UserDTO[]>{
    return this.http.get<UserDTO[]>(`${this.apiUrl}/friends/find?userId=${userId}&friendId=${receiverId}`);
  }

  getProfile(userId:any): Observable<UserDTO>{
    return this.http.get<UserDTO>(`${this.apiUrl}/profile/${userId}`);
  }

  getStatus(userId:any,receiverId:number) : Observable<String>{
    return this.http.get<String>(`${this.apiUrl}/friend-requests/relationship-status?userId=${userId}&friendId`)
  }
}
