import { Injectable } from '@angular/core';

import { HttpClient } from '@angular/common/http';

import { Observable } from 'rxjs';

export interface Friend {

  id: number;

  name: string;

  profilePic?: string;

}

@Injectable({

  providedIn: 'root'

})

export class FriendsService {

  private apiUrl = 'http://localhost:8765/api/friend-requests';

  constructor(private http: HttpClient) {}

  getFriends(userId:String): Observable<Friend[]> {
    return this.http.get<Friend[]>(`${this.apiUrl}/friends/${userId}`);
  }

  getFriendRequestsReceived(userId:string): Observable<Friend[]> {
    return this.http.get<Friend[]>(`${this.apiUrl}/received/${userId}`);
  }

  getFriendRequestsSent(userId:string): Observable<Friend[]> {
    return this.http.get<Friend[]>(`${this.apiUrl}/sent/${userId}`);
  }

  sendFriendRequest(userId:string, receiverId: number): Observable<any> {

    return this.http.post(`${this.apiUrl}/send?senderId=${userId}&receiverId=${receiverId}`,{});

  }

  acceptFriendRequest(requestId: number): Observable<any> {

    return this.http.put(`${this.apiUrl}/accept/${requestId}`, {});

  }

  rejectFriendRequest(requestId: number): Observable<any> {

    return this.http.put(`${this.apiUrl}/reject/${requestId}`, {});

  }

  cancelFriendRequest(requestId: number): Observable<any> {

    return this.http.delete(`${this.apiUrl}/cancel/${requestId}`);

  }

  findFriends(userId: number): Observable<Friend[]> {

    return this.http.get<Friend[]>(`${this.apiUrl}/friends/find/${userId}`);

  }

  unFriend(userId:number,receiverId:number):Observable<any>{
      return this.http.delete(`${this.apiUrl}/friends/delete?userId${userId}&friendId${receiverId}`);
  }

}
