import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { Friend, FriendsService, Request } from 'src/app/core/services/Friends/friends.service';

@Component({
  selector: 'app-friend-request-received',
  templateUrl: './friend-request-received.component.html',
  styleUrls: ['./friend-request-received.component.css']
})

export class FriendRequestReceivedComponent implements OnInit {

  requests: Request[] = [];
  filteredRequests:Request[]=[];
  searchText:string='';
  constructor(private friendsService: FriendsService) {}

  ngOnInit(): void {

    this.loadRequests();

  }

  loadRequests(): void {

    this.friendsService.getFriendRequestsReceived(localStorage.getItem('userId')).subscribe({

      next: data => {
        this.requests=data;
        this.filteredRequests=data;
      },

      error: err => console.error('Error loading received requests', err)

    });

  }

   onSearchChange(): void {

    const text = this.searchText.toLowerCase();

    this.requests = this.filteredRequests.filter(request =>
      request.sender.username.toLowerCase().includes(text)
    );

  }

  accept(id: number): void {

    this.friendsService.acceptFriendRequest(id,localStorage.getItem('userId')).subscribe({
      next:data=>alert(data),
      error:err=>alert("Request can't be accepted")   
    });

  }

  reject(id: number): void {

    this.friendsService.rejectFriendRequest(id,localStorage.getItem('userId')).subscribe({
      next:data=>alert(data),
      error:err=>alert("Request can't be rejected")   
    });

  }

}
