import { Component } from '@angular/core';
import { Router } from '@angular/router';

type EnabledField='name'|'dob'|'bio';

@Component({
  selector: 'app-profile-edit',
  templateUrl: './profile-edit.component.html',
  styleUrls: ['./profile-edit.component.css']
})


export class ProfileEditComponent {
  name = "Anjali Sharma";
  dob = "1996-08-15"; // example DOB
  bio = "Infosys Employee üíª";
  avatar = "assets/images/avatar.jpg";

  editMode:Record<EnabledField,boolean> = { name: false, dob: false, bio: false };

  constructor(private router: Router) {}

  toggleEdit(field: EnabledField) {
    this.editMode[field] = !this.editMode[field];
  }

  onFileSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.avatar = e.target.result; // Preview uploaded pic
      };
      reader.readAsDataURL(file);
    }
  }

  removePicture() {
    this.avatar = "assets/images/default-avatar.png"; // fallback image
  }

  saveProfile() {
    alert('Profile saved successfully! (Dummy for now)');
    this.router.navigate(['/profile']);
  }
}
